int compare(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

float calculateMedian(int arr[], int n) {
    // Sort the array
    qsort(arr, n, sizeof(int), compare);

    // Check if the number of elements is even or odd
    if (n % 2 != 0) {
