        assert_eq!(stack.get_min(), Some(&3));

        stack.push(2);
        stack.push(1);
        assert_eq!(stack.get_min(), Some(&1));

        stack.pop();
        assert_eq!(stack.get_min(), Some(&2));

        stack.pop();
        assert_eq!(stack.get_min(), Some(&3));
    }

    #[test]
    fn test_empty_stack() {
        let mut stack: MinStack<i32> = MinStack::new();
        assert_eq!(stack.get_min(), None);
        assert_eq!(stack.pop(), None);
    }
}
