import java.util.*;

public class ModeCalculator {
    public static void main(String[] args) {
        List<Integer> numbers = Arrays.asList(1, 2, 3, 2, 4, 2, 5, 3, 2);
        List<Integer> modes = findMode(numbers);

        System.out.println("Mode(s): " + modes);
