    public static double calculateMedian(List<Double> numbers) {
        // Step 1: Sort the list
        numbers.sort(Double::compareTo);

        int size = numbers.size();
        // Step 2: Check if the size is even or odd
        if (size % 2 == 0) {
            // For even size, average the two middle numbers
            double lower = numbers.get(size / 2 - 1);
            double upper = numbers.get(size / 2);
            return (lower + upper) / 2.0;
        } else {
            // For odd size, return the middle number
            return numbers.get(size / 2);
        }
    }
}
